export function getCitiesByRegion(regionId: RegionId): CityDefinition[] {
  return CITIES.filter(city => city.regionId === regionId);
}

export function getCityById(cityId: string): CityDefinition | undefined {
  return CITIES.find(city => city.id === cityId);
}

export function getAllCityIds(): string[] {
  return CITIES.map(city => city.id);
}

export function getCityCount(): number {
  return CITIES.length;
}

export function getRegionCityCount(regionId: RegionId): number {
  return getCitiesByRegion(regionId).length;
}
